import React, { Component } from 'react'
import './Catalog.scss'
import Discount from '../../Components/Discount/Discount'
import { FaShoppingCart, FaEye } from 'react-icons/fa'
import Client from '../../Contentful'

export default class Catalog extends Component {

  constructor(props) {
    super(props);
    this.state = {
      shoes:[]
    };
  }

  getData = async () => {
    try {
      let response = await Client.getEntries({
        content_type: 'product'
      })
      this.setState({
          shoes: response.items
      })

    } catch (error) {

    }
  }

  componentDidMount(){
    this.getData()
  }

  render() {
    let { shoes } = this.state

    return (
      <div className='Container__catalog-wrapper'>
        {shoes.map(item => {
          let isPromo
          let beforePrice

          item.fields.promo ? isPromo = <div className='promo'>{item.fields.discount}%<small className='title'>OFF</small></div> : isPromo = null
          item.fields.promo ? beforePrice = <small className='before'>${item.fields.price.toFixed(1)}</small> : beforePrice = null

          return (
            <div className='Container__catalog-product' key={item.fields.sku}>
              {isPromo}
              <div className='detail'>
                <strong className='name'>{item.fields.name}</strong>
                <div className='note'>
                  <p className='description'>{item.fields.description}</p>
                </div>
                <div className='recomendations'>
                  <small className='title'>Its a match</small>
                  <div className='match'>
                    <div className='product'>
                      <img src='https://via.placeholder.com/40x40' alt='its a match' />
                    </div>
                    <div className='product'>
                      <img src='https://via.placeholder.com/40x40' alt='its a match' />
                    </div>
                    <div className='product'>
                      <img src='https://via.placeholder.com/40x40' alt='its a match' />
                    </div>
                  </div>
                </div>
                <div className='action'>
                  <div className='addtocart'>
                    <FaShoppingCart size='1.6em' className='iconic' />
                  </div>
                  <div className='viewpdp'>
                    <FaEye size='1.6em' className='iconic' />
                  </div>
                </div>
              </div>
              <div className='body'>
                <div className='picture'>
                  <img src={item.fields.photo.fields.file.url} alt={item.fields.short_description} />
                </div>
              </div>
              <div className='footer'>
                <div className='info'>
                  <strong className='name'>{item.fields.name}</strong>
                  <small className='desc'>{item.fields.short_description}</small>
                </div>
                <div className='price'>
                  {beforePrice}
                  <span>$<Discount original_price={item.fields.price} discount_applied={item.fields.discount} /></span>
                </div>
              </div>
            </div>
          )
        })}
      </div>
    )
  }
}
