import React, { Component } from 'react'
import './Footer.scss'
import { FaInstagram, FaFacebookF, FaYoutube } from 'react-icons/fa'

export default class Footer extends Component {

  constructor(props) {
    super(props);
    this.state = {
      company: [
        'About us',
        'Story',
        'Benefits',
        'FAQs',
      ],
      client: [
        'Client service',
        'Purchase',
        'Assistance',
        'Devilvery',
        'Payment',
        'Claims'
      ],
      legal: [
        'Legal',
        'Terms & Conditions',
        'Privacy Policy'
      ]

    }
  }

  render() {

    let { company, client, legal } = this.state

    return (
      <div className='Container__footer-wrapper'>
        <div className="Container__footer-info">
          <div className='brand'>
            <img src="https://via.placeholder.com/100x30" alt="Shoz Og"/>
            <ul className='rss-list'>
              <li className='rss'><a href="#"><FaInstagram size='1.2em' /></a></li>
              <li className='rss'><a href="#"><FaFacebookF size='1.2em' /></a></li>
              <li className='rss'><a href="#"><FaYoutube size='1.2em' /></a></li>
            </ul>
          </div>
          <div className='block'>
            <ul className='menu'>
              {company.map(item => {
                return (
                  <li className='item'><a className='anchor' href="#">{item}</a></li>
                )
              })}
            </ul>
          </div>
          <div className='block'>
            <ul className='menu'>
              {client.map(item => {
                return (
                  <li className='item'><a className='anchor' href="#">{item}</a></li>
                )
              })}
            </ul>
          </div>
          <div className='block'>
            <ul className='menu'>
              {legal.map(item => {
                return (
                  <li className='item'><a className='anchor' href="#">{item}</a></li>
                )
              })}
            </ul>
          </div>
        </div>
      </div>
    )
  }
}
