import React, { Component } from 'react'

export default class Footer extends Component {

  constructor(props) {
    super(props);
    this.state = {
      company: [
        {
          title: 'About us',
          story: 'Story',
          benefits: 'Benefits',
          faq: 'FAQs'
        }
      ],
      client: [
        {
          title: 'Client service',
          purchase: 'Purchase',
          assistance: 'Assistance',
          delivery: 'Devilvery',
          payment: 'Payment',
          claim: 'Claims'
        }
      ],
      legal: [
        {
          title: 'Legal',
          terms: 'Terms & Conditions',
          privacy: 'Privacy Policy'
        }
      ]
    }
  }

  render() {

    let { company, client, legal } = this.state

    return (
      <div className='Container__footer-wrapper'>
        <div className="Container__footer-info">
          <div className='item'>
            {company.map(item => {
              return (
                <li>{item}</li>
              )
            })}
          </div>
          <div className='item'>
            {client.map(item => {
              return (
                <li>{item}</li>
              )
            })}
          </div>
          <div className='item'>
            {legal.map(item => {
              return (
                <li>{item}</li>
              )
            })}
          </div>
        </div>
      </div>
    )
  }
}
