import React, { Component } from 'react'
import './QuickReview.scss'
import Button from '../../Components/Button/Button'
import Client from '../../Contentful'

export default class Quick_review extends Component {

  constructor(props) {
    super(props);
    this.state = {}
  }

  getData = async () => {
    try {
      let response = await Client.getEntries({
        content_type: 'newProductReview'
      })

      this.setState(response.items)

    } catch (error) {

    }
  }

  componentDidMount() {
    this.getData()
  }

  render() {

    let { photoURL, title, review } = this.state

    return (
      <div className='Container__quickreview-wrapper'>
        <div className='Container__quickreview-counter'>
          <img src={photoURL} class='product' alt='New product review' />
        </div>
        <div className='Container__quickreview-review'>
          <div className='note'>
            <h1>{title}</h1>
            <p>{review}</p>
            <Button
              subject='secondary'
              text="I'm Interested"
            />
          </div>
        </div>
      </div>
    )
  }
}
